00580 void IRsend::customDelayMicroseconds(unsigned long aMicroseconds) {
00581 #if defined(__AVR__)
00582     unsigned long start = micros() - (64 / clockCyclesPerMicrosecond()); // - (64 / clockCyclesPerMicrosecond()) for reduced resolution and additional overhead
00583 #else
00584     unsigned long start = micros();
00585 #endif
00586     // overflow invariant comparison :-)
00587     while (micros() - start < aMicroseconds) {
00588     }
00589 }
