00603 void IRsend::enableIROut(uint8_t aFrequencyKHz) {
00604 #if defined(SEND_PWM_BY_TIMER)
00605     TIMER_DISABLE_RECEIVE_INTR; // Just in case receiving was active
00606     timerConfigForSend(aFrequencyKHz);
00607 
00608 #elif defined(USE_NO_SEND_PWM)
00609     (void) aFrequencyKHz;
00610 
00611 #else
00612     periodTimeMicros = (1000U + (aFrequencyKHz / 2)) / aFrequencyKHz; // rounded value -> 26 for 38.46 kHz, 27 for 37.04 kHz, 25 for 40 kHz.
00613 #  if defined(IR_SEND_PIN)
00614     periodOnTimeMicros = (((periodTimeMicros * IR_SEND_DUTY_CYCLE) + 50) / 100U); // +50 for rounding -> 830/100 for 30% and 16 MHz
00615 #  else
00616     periodOnTimeMicros = (((periodTimeMicros * IR_SEND_DUTY_CYCLE) + 50 - (PULSE_CORRECTION_NANOS / 10)) / 100U); // +50 for rounding -> 530/100 for 30% and 16 MHz
00617 #  endif
00618 #endif
00619 
00620 #if defined(USE_OPEN_DRAIN_OUTPUT_FOR_SEND_PIN)
00621 #  if defined(OUTPUT_OPEN_DRAIN)
00622     pinMode(sendPin, OUTPUT_OPEN_DRAIN); // the only place where this mode is set for sendPin
00623 #  endif // the mode INPUT for mimicking open drain is set at IRLedOff()
00624 
00625 #else
00626     pinModeFast(sendPin, OUTPUT);
00627 #endif
00628     IRLedOff(); // When not sending, we want it low/inactive
00629 }
00630 
00631 unsigned int IRsend::getPulseCorrectionNanos() {
00632     return PULSE_CORRECTION_NANOS;
00633 }
