00091 /*
00092  * Send repeat
00093  * Repeat commands should be sent in a 110 ms raster.
00094  * @param aCommand one of LG_COMMAND_OFF, LG_COMMAND_ON etc.
00095  */
00096 bool Aircondition_LG::sendCommandAndParameter(char aCommand, int aParameter) {
00097     // Commands without parameter
00098     switch (aCommand) {
00099     case LG_COMMAND_OFF: // off
00100         sendIRCommand(LG_POWER_DOWN);
00101         PowerIsOn = false;
00102         return true;
00103 
00104     case LG_COMMAND_ON: // on
00105         PowerIsOn = false; // set to false in order to suppress on bit
00106         sendTemperatureFanSpeedAndMode();
00107         return true;
00108 
00109     case LG_COMMAND_JET:
00110         IR_DEBUG_PRINTLN(F("Send jet on"));
00111         sendIRCommand(LG_JET_ON);
00112         return true;
00113 
00114     case LG_COMMAND_LIGHT:
00115         sendIRCommand(LG_LIGHT);
00116         return true;
00117 
00118     case LG_COMMAND_CLEAR_ALL:
00119         sendIRCommand(LG_CLEAR_ALL);
00120         return true;
00121 
00122     case LG_COMMAND_TEMPERATURE_PLUS:
00123         if (18 <= Temperature && Temperature <= 29) {
00124             Temperature++;
00125             sendTemperatureFanSpeedAndMode();
00126         } else {
00127             return false;
00128         }
00129         return true;
00130 
00131     case LG_COMMAND_TEMPERATURE_MINUS:
00132         if (19 <= Temperature && Temperature <= 30) {
00133             Temperature--;
00134             sendTemperatureFanSpeedAndMode();
00135         } else {
00136             return false;
00137         }
00138         return true;
00139 
00140     }
00141 
00142     PowerIsOn = true;
00143 
00144     /*
00145      * Now the commands which require a parameter
00146      */
00147     if (aParameter < 0) {
00148         IR_DEBUG_PRINT(F("Error: Parameter is less than 0"));
00149         return false;
00150     }
00151     switch (aCommand) {
00152 
00153     case LG_COMMAND_MODE:
00154         Mode = aParameter + '0';
00155         sendTemperatureFanSpeedAndMode();
00156         break;
00157 
00158     case LG_COMMAND_SWING:
00159         IR_DEBUG_PRINT(F("Send air swing="));
00160         IR_DEBUG_PRINTLN(aParameter);
00161         if (ACIsWallType) {
00162             if (aParameter) {
00163                 sendIRCommand(LG_WALL_SWING_ON);
00164             } else {
00165                 sendIRCommand(LG_WALL_SWING_OFF);
00166             }
00167         } else {
00168             if (aParameter) {
00169                 sendIRCommand(LG_SWING_ON);
00170             } else {
00171                 sendIRCommand(LG_SWING_OFF);
00172             }
00173         }
00174         break;
00175 
00176     case LG_COMMAND_AUTO_CLEAN:
00177         IR_DEBUG_PRINT(F("Send auto clean="));
00178         IR_DEBUG_PRINTLN(aParameter);
00179         if (aParameter) {
00180             sendIRCommand(LG_AUTO_CLEAN_ON);
00181         } else {
00182             sendIRCommand(LG_AUTO_CLEAN_OFF);
00183         }
00184         break;
00185 
00186     case LG_COMMAND_ENERGY:
00187         IR_DEBUG_PRINT(F("Send energy saving on="));
00188         IR_DEBUG_PRINTLN(aParameter);
00189         if (aParameter) {
00190             sendIRCommand(LG_ENERGY_SAVING_ON);
00191         } else {
00192             sendIRCommand(LG_ENERGY_SAVING_OFF);
00193         }
00194         break;
00195 
00196     case LG_COMMAND_FAN_SPEED:
00197         if (aParameter < SIZE_OF_FAN_SPEED_MAPPING_TABLE) {
00198             FanIntensity = aParameter;
00199             sendTemperatureFanSpeedAndMode();
00200         } else {
00201             return false;
00202         }
00203         break;
00204 
00205     case LG_COMMAND_TEMPERATURE:
00206         if (18 <= aParameter && aParameter <= 30) {
00207             Temperature = aParameter;
00208             sendTemperatureFanSpeedAndMode();
00209         } else {
00210             return false;
00211         }
00212         break;
00213 
00214     case LG_COMMAND_SLEEP:
00215         // 420 = maximum I have recorded
00216         if (aParameter <= 420) {
00217             sendIRCommand(LG_SLEEP + aParameter);
00218         } else {
00219             return false;
00220         }
00221         break;
00222 
00223     case LG_COMMAND_TIMER_ON:
00224         // 1440 = minutes of a day
00225         if (aParameter <= 1439) {
00226             sendIRCommand(LG_TIMER_ON + aParameter);
00227         } else {
00228             return false;
00229         }
00230         break;
00231 
00232     case LG_COMMAND_TIMER_OFF:
00233         if (aParameter <= 1439) {
00234             sendIRCommand(LG_TIMER_OFF + aParameter);
00235         } else {
00236             return false;
00237         }
00238         break;
00239 
00240     default:
00241         return false;
00242     }
00243     return true;
00244 }
