00246 void Aircondition_LG::sendIRCommand(uint16_t aCommand) {
00247 
00248     IR_INFO_PRINT(F("Send code=0x"));
00249     IR_INFO_PRINT(aCommand, HEX);
00250     IR_INFO_PRINT(F(" | 0b"));
00251     IR_INFO_PRINTLN(aCommand, BIN);
00252 
00253     IrSender.sendLG((uint8_t) LG_ADDRESS, aCommand, 0, false, useLG2Protocol);
00254 }
