00256 /*
00257  * Takes values from static variables
00258  */
00259 void Aircondition_LG::sendTemperatureFanSpeedAndMode() {
00260 
00261     uint8_t tTemperature = Temperature;
00262     IR_INFO_PRINT(F("Send temperature="));
00263     IR_INFO_PRINT(tTemperature);
00264     IR_INFO_PRINT(F(" fan intensity="));
00265     IR_INFO_PRINT(FanIntensity);
00266     IR_INFO_PRINT(F(" mode="));
00267     IR_INFO_PRINTLN((char )Mode);
00268 
00269     WordUnion tIRCommand;
00270     tIRCommand.UWord = 0;
00271 
00272     // Temperature is coded in the upper nibble of the LowByte
00273     tIRCommand.UByte.LowByte = ((tTemperature - 15) << 4); // 16 -> 0x00, 18 -> 0x30, 30 -> 0xF0
00274 
00275     // Fan intensity is coded in the lower nibble of the LowByte
00276     if (ACIsWallType) {
00277         tIRCommand.UByte.LowByte |= AC_FAN_WALL[FanIntensity];
00278     } else {
00279         tIRCommand.UByte.LowByte |= AC_FAN_TOWER[FanIntensity];
00280     }
00281 
00282     switch (Mode) {
00283     case AC_MODE_COOLING:
00284         tIRCommand.UByte.HighByte = LG_MODE_COOLING >> 8;
00285         break;
00286     case AC_MODE_HEATING:
00287         tIRCommand.UByte.HighByte = LG_MODE_HEATING >> 8;
00288         break;
00289     case AC_MODE_AUTO:
00290         tIRCommand.UByte.HighByte = LG_MODE_AUTO >> 8;
00291         break;
00292     case AC_MODE_FAN:
00293         tTemperature = 18;
00294         tIRCommand.UByte.HighByte = LG_MODE_FAN >> 8;
00295         break;
00296     case AC_MODE_DEHUMIDIFIYING:
00297         tIRCommand.UWord = LG_MODE_DEHUMIDIFIYING;
00298         break;
00299     default:
00300         break;
00301     }
00302     if (!PowerIsOn) {
00303         // switch on requires masked bit
00304         tIRCommand.UByte.HighByte &= ~(LG_SWITCH_ON_MASK >> 8);
00305     }
00306     PowerIsOn = true;
00307 
00308     sendIRCommand(tIRCommand.UWord);
00309 }
