00112 void IRsend::sendLG(uint8_t aAddress, uint16_t aCommand, uint_fast8_t aNumberOfRepeats, bool aIsRepeat, bool aUseLG2Protocol) {
00113     uint32_t tRawData = ((uint32_t) aAddress << (LG_COMMAND_BITS + LG_CHECKSUM_BITS)) | ((uint32_t) aCommand << LG_CHECKSUM_BITS);
00114     /*
00115      * My guess of the 4 bit checksum
00116      * Addition of all 4 nibbles of the 16 bit command
00117      */
00118     uint8_t tChecksum = 0;
00119     uint16_t tTempForChecksum = aCommand;
00120     for (int i = 0; i < 4; ++i) {
00121         tChecksum += tTempForChecksum & 0xF; // add low nibble
00122         tTempForChecksum >>= 4; // shift by a nibble
00123     }
00124     tRawData |= (tChecksum & 0xF);
00125     sendLGRaw(tRawData, aNumberOfRepeats, aIsRepeat, aUseLG2Protocol);
00126 }
00127 
00128 /*
00129  * Here you can put your raw data, even one with "wrong" checksum
00130  */
00131 void IRsend::sendLGRaw(uint32_t aRawData, uint_fast8_t aNumberOfRepeats, bool aIsRepeat, bool aUseLG2Protocol) {
00132     if (aIsRepeat) {
00133         sendLGRepeat();
00134         return;
00135     }
00136     // Set IR carrier frequency
00137     enableIROut(LG_KHZ);
00138 
00139     // Header
00140     if (aUseLG2Protocol) {
00141         mark(LG2_HEADER_MARK);
00142         space(LG2_HEADER_SPACE);
00143         // MSB first
00144         sendPulseDistanceWidthData(LG_BIT_MARK, LG_ONE_SPACE, LG_BIT_MARK, LG_ZERO_SPACE, aRawData, LG_BITS, PROTOCOL_IS_MSB_FIRST,
00145         SEND_STOP_BIT);
00146     } else {
00147         mark(LG_HEADER_MARK);
00148         space(LG_HEADER_SPACE);
00149         // MSB first
00150         sendPulseDistanceWidthData(LG_BIT_MARK, LG_ONE_SPACE, LG_BIT_MARK, LG_ZERO_SPACE, aRawData, LG_BITS, PROTOCOL_IS_MSB_FIRST,
00151         SEND_STOP_BIT);
00152     }
00153 
00154     for (uint_fast8_t i = 0; i < aNumberOfRepeats; ++i) {
00155         // send repeat in a 110 ms raster
00156         if (i == 0) {
00157             delay(LG_REPEAT_SPACE / MICROS_IN_ONE_MILLI);
00158         } else {
00159             delay((LG_REPEAT_PERIOD - LG_REPEAT_DURATION) / MICROS_IN_ONE_MILLI);
00160         }
00161         // send repeat
00162         sendLGRepeat(aUseLG2Protocol);
00163     }
00164 }
