00169 /*
00170  * First check for right data length
00171  * Next check start bit
00172  * Next try the decode
00173  * Last check stop bit
00174  */
00175 bool IRrecv::decodeLG() {
00176     decode_type_t tProtocol = LG;
00177     uint16_t tHeaderSpace = LG_HEADER_SPACE;
00178 
00179 // Check we have the right amount of data (60). The +4 is for initial gap, start bit mark and space + stop bit mark.
00180     if (decodedIRData.rawDataPtr->rawlen != ((2 * LG_BITS) + 4) && (decodedIRData.rawDataPtr->rawlen != 4)) {
00181         IR_DEBUG_PRINT(F("LG: "));
00182         IR_DEBUG_PRINT("Data length=");
00183         IR_DEBUG_PRINT(decodedIRData.rawDataPtr->rawlen);
00184         IR_DEBUG_PRINTLN(" is not 60 or 4");
00185         return false;
00186     }
00187 
00188 // Check header "mark" this must be done for repeat and data
00189     if (!matchMark(decodedIRData.rawDataPtr->rawbuf[1], LG_HEADER_MARK)) {
00190         if (!matchMark(decodedIRData.rawDataPtr->rawbuf[1], LG2_HEADER_MARK)) {
00191             IR_DEBUG_PRINT(F("LG: "));
00192             IR_DEBUG_PRINTLN("Header mark is wrong");
00193             return false;
00194         } else {
00195             tProtocol = LG2;
00196             tHeaderSpace = LG2_HEADER_SPACE;
00197         }
00198     }
00199 
00200 // Check for repeat - here we have another header space length
00201     if (decodedIRData.rawDataPtr->rawlen == 4) {
00202         if (matchSpace(decodedIRData.rawDataPtr->rawbuf[2], LG_REPEAT_HEADER_SPACE)
00203                 && matchMark(decodedIRData.rawDataPtr->rawbuf[3], LG_BIT_MARK)) {
00204             decodedIRData.flags = IRDATA_FLAGS_IS_REPEAT | IRDATA_FLAGS_IS_MSB_FIRST;
00205             decodedIRData.address = lastDecodedAddress;
00206             decodedIRData.command = lastDecodedCommand;
00207             decodedIRData.protocol = lastDecodedProtocol;
00208             return true;
00209         }
00210         IR_DEBUG_PRINT(F("LG: "));
00211         IR_DEBUG_PRINT("Repeat header space is wrong");
00212         return false;
00213     }
00214 
00215 // Check command header space
00216     if (!matchSpace(decodedIRData.rawDataPtr->rawbuf[2], tHeaderSpace)) {
00217         IR_DEBUG_PRINT(F("LG: "));
00218         IR_DEBUG_PRINTLN(F("Header space length is wrong"));
00219         return false;
00220     }
00221 
00222     if (!decodePulseDistanceData(LG_BITS, 3, LG_BIT_MARK, LG_ONE_SPACE, LG_ZERO_SPACE, PROTOCOL_IS_MSB_FIRST)) {
00223         IR_DEBUG_PRINT(F("LG: "));
00224         IR_DEBUG_PRINTLN(F("Decode failed"));
00225         return false;
00226     }
00227 
00228 // Stop bit
00229     if (!matchMark(decodedIRData.rawDataPtr->rawbuf[3 + (2 * LG_BITS)], LG_BIT_MARK)) {
00230         IR_DEBUG_PRINT(F("LG: "));
00231         IR_DEBUG_PRINTLN(F("Stop bit mark length is wrong"));
00232         return false;
00233     }
00234 
00235 // Success
00236     decodedIRData.flags = IRDATA_FLAGS_IS_MSB_FIRST;
00237     decodedIRData.command = (decodedIRData.decodedRawData >> LG_CHECKSUM_BITS) & 0xFFFF;
00238     decodedIRData.address = decodedIRData.decodedRawData >> (LG_COMMAND_BITS + LG_CHECKSUM_BITS); // first 8 bit
00239 
00240     /*
00241      * My guess of the checksum
00242      */
00243     uint8_t tChecksum = 0;
00244     uint16_t tTempForChecksum = decodedIRData.command;
00245     for (int i = 0; i < 4; ++i) {
00246         tChecksum += tTempForChecksum & 0xF; // add low nibble
00247         tTempForChecksum >>= 4; // shift by a nibble
00248     }
00249 // Checksum check
00250     if ((tChecksum & 0xF) != (decodedIRData.decodedRawData & 0xF)) {
00251         IR_DEBUG_PRINT(F("LG: "));
00252         IR_DEBUG_PRINT("4 bit checksum is not correct. expected=0x");
00253         IR_DEBUG_PRINT(tChecksum, HEX);
00254         IR_DEBUG_PRINT(" received=0x");
00255         IR_DEBUG_PRINT((decodedIRData.decodedRawData & 0xF), HEX);
00256         IR_DEBUG_PRINT(" data=0x");
00257         IR_DEBUG_PRINTLN(decodedIRData.command, HEX);
00258         decodedIRData.flags |= IRDATA_FLAGS_PARITY_FAILED;
00259     }
00260 
00261     decodedIRData.protocol = tProtocol; // LG or LG2
00262     decodedIRData.numberOfBits = LG_BITS;
00263 
00264     return true;
00265 }
