00267 bool IRrecv::decodeLGMSB(decode_results *aResults) {
00268     unsigned int offset = 1; // Skip first space
00269 
00270 // Check we have enough data (60) - +4 for initial gap, start bit mark and space + stop bit mark
00271     if (aResults->rawlen != (2 * LG_BITS) + 4) {
00272         return false;
00273     }
00274 
00275 // Initial mark/space
00276     if (!matchMark(aResults->rawbuf[offset], LG_HEADER_MARK)) {
00277         return false;
00278     }
00279     offset++;
00280 
00281     if (!matchSpace(aResults->rawbuf[offset], LG_HEADER_SPACE)) {
00282         return false;
00283     }
00284     offset++;
00285 
00286     if (!decodePulseDistanceData(LG_BITS, offset, LG_BIT_MARK, LG_ONE_SPACE, LG_ZERO_SPACE, PROTOCOL_IS_MSB_FIRST)) {
00287         return false;
00288     }
00289 // Stop bit
00290     if (!matchMark(aResults->rawbuf[offset + (2 * LG_BITS)], LG_BIT_MARK)) {
00291         IR_DEBUG_PRINTLN(F("Stop bit mark length is wrong"));
00292         return false;
00293     }
00294 
00295 // Success
00296     aResults->value = decodedIRData.decodedRawData;
00297     aResults->bits = LG_BITS;
00298     aResults->decode_type = LG;
00299     decodedIRData.protocol = LG;
00300     return true;
00301 }
