00304 void IRsend::sendLG(unsigned long data, int nbits) {
00305 // Set IR carrier frequency
00306     enableIROut(LG_KHZ);
00307 #if !(defined(__AVR_ATtiny25__) || defined(__AVR_ATtiny45__) || defined(__AVR_ATtiny85__) || defined(__AVR_ATtiny87__) || defined(__AVR_ATtiny167__))
00308     Serial.println(
00309             "The function sendLG(data, nbits) is deprecated and may not work as expected! Use sendLGRaw(data, NumberOfRepeats) or better sendLG(Address, Command, NumberOfRepeats).");
00310 #endif
00311 // Header
00312     mark(LG_HEADER_MARK);
00313     space(LG_HEADER_SPACE);
00314 //    mark(LG_BIT_MARK);
00315 
00316 // Data + stop bit
00317     sendPulseDistanceWidthData(LG_BIT_MARK, LG_ONE_SPACE, LG_BIT_MARK, LG_ZERO_SPACE, data, nbits, PROTOCOL_IS_MSB_FIRST,
00318     SEND_STOP_BIT);
00319 }
