00044 //==============================================================================
00045 //              SSSS   AAA    MMM    SSSS  U   U  N   N   GGGG
00046 //             S      A   A  M M M  S      U   U  NN  N  G
00047 //              SSS   AAAAA  M M M   SSS   U   U  N N N  G  GG
00048 //                 S  A   A  M   M      S  U   U  N  NN  G   G
00049 //             SSSS   A   A  M   M  SSSS    UUU   N   N   GGG
00050 //==============================================================================
00051 // see http://www.hifi-remote.com/wiki/index.php?title=DecodeIR#Samsung
00052 // https://www.mikrocontroller.net/articles/IRMP_-_english#SAMSUNG32
00053 // LSB first, 1 start bit + 16 bit address + 16,32,20 bit data + 1 stop bit.
00054 // repeats are like NEC but with 2 stop bits
00055 
00056 #define SAMSUNG_ADDRESS_BITS        16
00057 #define SAMSUNG_COMMAND16_BITS      16
00058 #define SAMSUNG_COMMAND32_BITS      32
00059 #define SAMSUNG_BITS                (SAMSUNG_ADDRESS_BITS + SAMSUNG_COMMAND16_BITS)
00060 #define SAMSUNG48_BITS              (SAMSUNG_ADDRESS_BITS + SAMSUNG_COMMAND32_BITS)
00061 
00062 #define SAMSUNG_UNIT                553 // 21 periods of 38 kHz (552,631)
00063 #define SAMSUNG_HEADER_MARK         (8 * SAMSUNG_UNIT) // 4400
00064 #define SAMSUNG_HEADER_SPACE        (8 * SAMSUNG_UNIT) // 4400
00065 #define SAMSUNG_BIT_MARK            SAMSUNG_UNIT
00066 #define SAMSUNG_ONE_SPACE           (3 * SAMSUNG_UNIT) // 1650
00067 #define SAMSUNG_ZERO_SPACE          SAMSUNG_UNIT
00068 
00069 #define SAMSUNG_AVERAGE_DURATION    55000 // SAMSUNG_HEADER_MARK + SAMSUNG_HEADER_SPACE  + 32 * 2,5 * SAMSUNG_UNIT + SAMSUNG_UNIT // 2.5 because we assume more zeros than ones
00070 #define SAMSUNG_REPEAT_DURATION     (SAMSUNG_HEADER_MARK  + SAMSUNG_HEADER_SPACE + SAMSUNG_BIT_MARK + SAMSUNG_ZERO_SPACE + SAMSUNG_BIT_MARK)
00071 #define SAMSUNG_REPEAT_PERIOD       110000 // Commands are repeated every 110 ms (measured from start to start) for as long as the key on the remote control is held down.
00072 
00073 
00078 void IRsend::sendSamsungRepeat() {
00079     enableIROut(SAMSUNG_KHZ); // 38 kHz
00080     mark(SAMSUNG_HEADER_MARK);
00081     space(SAMSUNG_HEADER_SPACE);
00082     mark(SAMSUNG_BIT_MARK);
00083     space(SAMSUNG_ZERO_SPACE);
00084     mark(SAMSUNG_BIT_MARK);
00085 }
00086 
00087 void IRsend::sendSamsung(uint16_t aAddress, uint16_t aCommand, uint_fast8_t aNumberOfRepeats, bool aIsRepeat) {
00088     if (aIsRepeat) {
00089         sendSamsungRepeat();
00090         return;
00091     }
00092 
00093     // Set IR carrier frequency
00094     enableIROut(SAMSUNG_KHZ);
00095 
00096     // Header
00097     mark(SAMSUNG_HEADER_MARK);
00098     space(SAMSUNG_HEADER_SPACE);
00099 
00100     // Address
00101     sendPulseDistanceWidthData(SAMSUNG_BIT_MARK, SAMSUNG_ONE_SPACE, SAMSUNG_BIT_MARK, SAMSUNG_ZERO_SPACE, aAddress,
00102     SAMSUNG_ADDRESS_BITS, PROTOCOL_IS_LSB_FIRST);
00103 
00104     // Command
00105 
00106     // send 8 command bits and then 8 inverted command bits LSB first
00107     aCommand = aCommand & 0xFF;
00108     aCommand = ((~aCommand) << 8) | aCommand;
00109 
00110     sendPulseDistanceWidthData(SAMSUNG_BIT_MARK, SAMSUNG_ONE_SPACE, SAMSUNG_BIT_MARK, SAMSUNG_ZERO_SPACE, aCommand,
00111     SAMSUNG_COMMAND16_BITS, PROTOCOL_IS_LSB_FIRST, SEND_STOP_BIT);
00112 
00113     for (uint_fast8_t i = 0; i < aNumberOfRepeats; ++i) {
00114         // send repeat in a 110 ms raster
00115         if (i == 0) {
00116             delay((SAMSUNG_REPEAT_PERIOD - SAMSUNG_AVERAGE_DURATION) / MICROS_IN_ONE_MILLI);
00117         } else {
00118             delay((SAMSUNG_REPEAT_PERIOD - SAMSUNG_REPEAT_DURATION) / MICROS_IN_ONE_MILLI);
00119         }
00120         // send repeat
00121         sendSamsungRepeat();
00122     }
00123 }
