00126 bool IRrecv::decodeSamsung() {
00127 
00128     // Check we have enough data (68). The +4 is for initial gap, start bit mark and space + stop bit mark
00129     if (decodedIRData.rawDataPtr->rawlen != ((2 * SAMSUNG_BITS) + 4)
00130             && decodedIRData.rawDataPtr->rawlen != ((2 * SAMSUNG48_BITS) + 4) && (decodedIRData.rawDataPtr->rawlen != 6)) {
00131         IR_DEBUG_PRINT(F("Samsung: "));
00132         IR_DEBUG_PRINT("Data length=");
00133         IR_DEBUG_PRINT(decodedIRData.rawDataPtr->rawlen);
00134         IR_DEBUG_PRINTLN(" is not 68 or 100 or 6");
00135         return false;
00136     }
00137 
00138     // Check header "mark" + "space"
00139     if (!matchMark(decodedIRData.rawDataPtr->rawbuf[1], SAMSUNG_HEADER_MARK)
00140             || !matchSpace(decodedIRData.rawDataPtr->rawbuf[2], SAMSUNG_HEADER_SPACE)) {
00141         IR_DEBUG_PRINT("Samsung: ");
00142         IR_DEBUG_PRINTLN("Header mark or space length is wrong");
00143 
00144         return false;
00145     }
00146 
00147     // Check for repeat
00148     if (decodedIRData.rawDataPtr->rawlen == 6) {
00149         decodedIRData.flags = IRDATA_FLAGS_IS_REPEAT| IRDATA_FLAGS_IS_LSB_FIRST;
00150         decodedIRData.address = lastDecodedAddress;
00151         decodedIRData.command = lastDecodedCommand;
00152         decodedIRData.protocol = SAMSUNG;
00153         return true;
00154     }
00155 
00156     if (decodedIRData.rawDataPtr->rawlen == (2 * SAMSUNG48_BITS) + 4) {
00157         /*
00158          * Samsung48
00159          */
00160         // decode address
00161         if (!decodePulseDistanceData(SAMSUNG_ADDRESS_BITS, 3, SAMSUNG_BIT_MARK, SAMSUNG_ONE_SPACE, SAMSUNG_ZERO_SPACE, PROTOCOL_IS_LSB_FIRST)) {
00162             IR_DEBUG_PRINT("Samsung: ");
00163             IR_DEBUG_PRINTLN("Decode failed");
00164             return false;
00165         }
00166         decodedIRData.address = decodedIRData.decodedRawData;
00167 
00168         // decode 32 bit command
00169         if (!decodePulseDistanceData(SAMSUNG_COMMAND32_BITS, 3, SAMSUNG_BIT_MARK, SAMSUNG_ONE_SPACE, SAMSUNG_ZERO_SPACE, PROTOCOL_IS_LSB_FIRST)) {
00170             IR_DEBUG_PRINT("Samsung: ");
00171             IR_DEBUG_PRINTLN("Decode failed");
00172             return false;
00173         }
00174 
00175         // Success
00176 //    decodedIRData.flags = IRDATA_FLAGS_IS_LSB_FIRST; // Not required, since this is the start value
00177         LongUnion tValue;
00178         tValue.ULong = decodedIRData.decodedRawData;
00179         // receive 2 * (8 bits then 8 inverted bits) LSB first
00180         if (tValue.UByte.HighByte != (uint8_t) (~tValue.UByte.MidHighByte)
00181                 && tValue.UByte.MidLowByte != (uint8_t) (~tValue.UByte.LowByte)) {
00182             decodedIRData.flags = IRDATA_FLAGS_PARITY_FAILED | IRDATA_FLAGS_IS_LSB_FIRST;
00183         }
00184         decodedIRData.command = tValue.UByte.HighByte << 8 | tValue.UByte.MidLowByte;
00185         decodedIRData.numberOfBits = SAMSUNG48_BITS;
00186 
00187     } else {
00188         /*
00189          * Samsung32
00190          */
00191         if (!decodePulseDistanceData(SAMSUNG_BITS, 3, SAMSUNG_BIT_MARK, SAMSUNG_ONE_SPACE, SAMSUNG_ZERO_SPACE, PROTOCOL_IS_LSB_FIRST)) {
00192             IR_DEBUG_PRINT("Samsung: ");
00193             IR_DEBUG_PRINTLN("Decode failed");
00194             return false;
00195         }
00196         LongUnion tValue;
00197         tValue.ULong = decodedIRData.decodedRawData;
00198         decodedIRData.address = tValue.UWord.LowWord;
00199 
00200         if (tValue.UByte.MidHighByte == (uint8_t) (~tValue.UByte.HighByte)) {
00201             // 8 bit command protocol
00202             decodedIRData.command = tValue.UByte.MidHighByte; // first 8 bit
00203         } else {
00204             // 16 bit command protocol
00205             decodedIRData.command = tValue.UWord.HighWord; // first 16 bit
00206         }
00207         decodedIRData.numberOfBits = SAMSUNG_BITS;
00208     }
00209 
00210     decodedIRData.protocol = SAMSUNG;
00211 
00212     return true;
00213 }
