00215 bool IRrecv::decodeSAMSUNG(decode_results *aResults) {
00216     unsigned int offset = 1;  // Skip first space
00217 
00218     // Initial mark
00219     if (!matchMark(aResults->rawbuf[offset], SAMSUNG_HEADER_MARK)) {
00220         return false;
00221     }
00222     offset++;
00223 
00224 // Check for repeat -- like a NEC repeat
00225     if ((aResults->rawlen == 4) && matchSpace(aResults->rawbuf[offset], 2250)
00226             && matchMark(aResults->rawbuf[offset + 1], SAMSUNG_BIT_MARK)) {
00227         aResults->bits = 0;
00228         aResults->value = 0xFFFFFFFF;
00229         decodedIRData.flags = IRDATA_FLAGS_IS_REPEAT;
00230         decodedIRData.protocol = SAMSUNG;
00231         return true;
00232     }
00233     if (aResults->rawlen < (2 * SAMSUNG_BITS) + 4) {
00234         return false;
00235     }
00236 
00237 // Initial space
00238     if (!matchSpace(aResults->rawbuf[offset], SAMSUNG_HEADER_SPACE)) {
00239         return false;
00240     }
00241     offset++;
00242 
00243     if (!decodePulseDistanceData(SAMSUNG_BITS, offset, SAMSUNG_BIT_MARK, SAMSUNG_ONE_SPACE, SAMSUNG_ZERO_SPACE, PROTOCOL_IS_MSB_FIRST)) {
00244         return false;
00245     }
00246 
00247 // Success
00248     aResults->value = decodedIRData.decodedRawData;
00249     aResults->bits = SAMSUNG_BITS;
00250     aResults->decode_type = SAMSUNG;
00251     decodedIRData.protocol = SAMSUNG;
00252     return true;
00253 }
