00256 void IRsend::sendSAMSUNG(unsigned long data, int nbits) {
00257     // Set IR carrier frequency
00258     enableIROut(SAMSUNG_KHZ);
00259 
00260     // Header
00261     mark(SAMSUNG_HEADER_MARK);
00262     space(SAMSUNG_HEADER_SPACE);
00263 
00264     // Old version with MSB first Data + stop bit
00265     sendPulseDistanceWidthData(SAMSUNG_BIT_MARK, SAMSUNG_ONE_SPACE, SAMSUNG_BIT_MARK, SAMSUNG_ZERO_SPACE, data, nbits, PROTOCOL_IS_MSB_FIRST,
00266     SEND_STOP_BIT);
00267 }
